import React, { useState, useEffect } from 'react';
import { fetchTags, searchWordsByTags } from '../services/api';
import './SearchByTag.css'; // Import the CSS file

function SearchByTag() {
  const [availableTags, setAvailableTags] = useState([]);
  const [selectedTags, setSelectedTags] = useState([]);
  const [results, setResults] = useState([]);
  const [error, setError] = useState(null); // State for handling errors

  useEffect(() => {
    // Fetch all available tags for selection
    fetchTags()
      .then(response => {
        if (Array.isArray(response.data)) {
          setAvailableTags(response.data);
          setError(null);
        } else {
          setError('Invalid tags data format');
        }
      })
      .catch(error => {
        console.error('Error fetching tags:', error);
        setError('Failed to fetch tags');
      });
  }, []);

  const handleTagChange = (e) => {
    setSelectedTags(Array.from(e.target.selectedOptions, option => option.value));
  };

  const handleSearch = () => {
    if (selectedTags.length === 0) {
      alert('Please select at least one tag.');
      return;
    }
    searchWordsByTags(`tag=${selectedTags.join(',')}`)
      .then(response => {
        if (Array.isArray(response.data)) {
          setResults(response.data);
        } else {
          setError('Invalid search results format');
        }
      })
      .catch(error => {
        console.error('Error fetching words by tags:', error);
        setError('Failed to fetch words');
      });
  };

  return (
    <div className="search-by-tag">
      <h2>Search Words by Tags</h2>
      {error && <p className="error-message">{error}</p>}
      <div className="form-group">
        <label>Select Tags:</label>
        <select multiple value={selectedTags} onChange={handleTagChange} className="tag-select">
          {availableTags.map(tag => (
            <option key={tag.name} value={tag.name}>
              {tag.name}
            </option>
          ))}
        </select>
        <button onClick={handleSearch} className="search-button">Search</button>
      </div>
      <div>
        <h3>Search Results</h3>
        <ul>
          {results.length === 0 ? (
            <p>No words found.</p>
          ) : (
            results.map(word => (
              <li key={word.word}>
                <strong>{word.word}</strong>: {word.description}<br />
                <em>Full Form:</em> {word.fullForm}<br />
                <em>Tags:</em> {word.tags.join(', ')}
              </li>
            ))
          )}
        </ul>
      </div>
    </div>
  );
}

export default SearchByTag;
