import React, { useState, useEffect } from 'react';
import { fetchTags, searchWordsByTags } from '../services/api';

function SearchByTag() {
  const [availableTags, setAvailableTags] = useState([]);
  const [selectedTags, setSelectedTags] = useState([]);
  const [results, setResults] = useState([]);

  useEffect(() => {
    // Fetch all available tags for selection
    fetchTags()
      .then(response => setAvailableTags(response.data))
      .catch(error => console.error('Error fetching tags:', error));
  }, []);

  const handleTagChange = (e) => {
    setSelectedTags(Array.from(e.target.selectedOptions, option => option.value));
  };

  const handleSearch = () => {
    if (selectedTags.length === 0) {
      alert('Please select at least one tag.');
      return;
    }
    searchWordsByTags(selectedTags.join(','))
      .then(response => setResults(response.data))
      .catch(error => console.error('Error fetching words by tags:', error));
  };

  return (
    <div>
      <h2>Search Words by Tags</h2>
      <div>
        <label>Select Tags:</label>
        <select multiple value={selectedTags} onChange={handleTagChange}>
          {availableTags.map(tag => (
            <option key={tag.name} value={tag.name}>
              {tag.name}
            </option>
          ))}
        </select>
        <button onClick={handleSearch}>Search</button>
      </div>
      <div>
        <h3>Search Results</h3>
        <ul>
          {results.length === 0 ? (
            <p>No words found.</p>
          ) : (
            results.map(word => (
              <li key={word.word}>
                <strong>{word.word}</strong>: {word.description}
              </li>
            ))
          )}
        </ul>
      </div>
    </div>
  );
}

export default SearchByTag;
