import React, { useState, useEffect } from 'react';
import { fetchWordDetails, updateWord, fetchTags } from '../services/api';
import { useParams } from 'react-router-dom';

function WordDetails() {
  const { word } = useParams();
  const [wordData, setWordData] = useState(null);
  const [tags, setTags] = useState([]);
  const [availableTags, setAvailableTags] = useState([]);
  const [loading, setLoading] = useState(true); // Loading state
  const [error, setError] = useState(null); // Error state

  useEffect(() => {
    const fetchData = async () => {
      try {
        const wordResponse = await fetchWordDetails(word);
        const tagsResponse = await fetchTags();

        if (wordResponse.data) {
          setWordData(wordResponse.data);
        } else {
          throw new Error('Invalid word data format');
        }

        if (Array.isArray(tagsResponse.data)) {
          setAvailableTags(tagsResponse.data);
        } else {
          throw new Error('Invalid tags data format');
        }
      } catch (err) {
        setError(err.message || 'Failed to fetch word details or tags');
        console.error('Error fetching data:', err);
      } finally {
        setLoading(false); // Set loading to false regardless of success or failure
      }
    };

    fetchData();
  }, [word]);

  const handleTagChange = (e) => {
    setTags(Array.from(e.target.selectedOptions, option => option.value));
  };

  const handleUpdate = async () => {
    try {
      await updateWord(word, { tags });
      alert('Tags updated successfully!');
    } catch (err) {
      alert('Failed to update tags: ' + (err.message || 'Unknown error'));
    }
  };

  if (loading) return <p>Loading...</p>; // Loading state
  if (error) return <p style={{ color: 'red' }}>{error}</p>; // Error state

  return (
    <div>
      <h2>{wordData.word}</h2>
      <p><strong>Full Form:</strong> {wordData.full_form}</p>
      <p><strong>Description:</strong> {wordData.description}</p>
      <div>
        <label>Edit Tags:</label>
        <select multiple value={tags} onChange={handleTagChange}>
          {availableTags.map((tag) => (
            <option key={tag.name} value={tag.name}>{tag.name}</option>
          ))}
        </select>
        <button onClick={handleUpdate}>Update Tags</button>
      </div>
    </div>
  );
}

export default WordDetails;
