import React, { useState, useEffect } from 'react';
import { fetchWordDetails, updateWord, fetchTags } from '../services/api';
import { useParams } from 'react-router-dom';

function WordDetails() {
  const { word } = useParams();
  const [wordData, setWordData] = useState(null);
  const [tags, setTags] = useState([]);
  const [availableTags, setAvailableTags] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const wordResponse = await fetchWordDetails(word);
        const tagsResponse = await fetchTags();

        // Assuming the wordResponse is directly the word object
        setWordData(wordResponse.data); // Adjust based on your actual API response structure
        setAvailableTags(tagsResponse.data); // Assuming this is an array of tags
      } catch (err) {
        setError('Failed to fetch word details or tags: ' + (err.message || err));
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [word]);

  const handleTagChange = (e) => {
    setTags(Array.from(e.target.selectedOptions, option => option.value));
  };

  const handleUpdate = async () => {
    try {
      await updateWord(word, { tags });
      alert('Tags updated successfully!');
    } catch (err) {
      alert('Failed to update tags: ' + (err.message || 'Unknown error'));
    }
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  return (
    <div>
      <h2>{wordData.word}</h2>
      <p><strong>Full Form:</strong> {wordData.fullForm}</p>
      <p><strong>Description:</strong> {wordData.description}</p>
      <div>
        <label>Edit Tags:</label>
        <select multiple value={tags} onChange={handleTagChange}>
          {availableTags.map((tag) => (
            <option key={tag.name} value={tag.name}>{tag.name}</option>
          ))}
        </select>
        <button onClick={handleUpdate}>Update Tags</button>
      </div>
    </div>
  );
}

export default WordDetails;
