import axios from 'axios';

const API_BASE_URL = 'https://c9f7b82a-b412-4ec3-ac6d-a18f0709a49d.mock.pstmn.io'; // Replace with actual backend URL

// Fetch all words
export const fetchWords = () => axios.get(`${API_BASE_URL}/words`);

// Fetch details of a word
export const fetchWordDetails = (word) => axios.get(`${API_BASE_URL}/words/${word}`);

// Add a new word
export const addWord = (wordData) => axios.post(`${API_BASE_URL}/words`, wordData);

// Update a word's tags or description
export const updateWord = (word, updatedData) => axios.put(`${API_BASE_URL}/words/${word}`, updatedData);

// Fetch top N searched words
export const fetchTopSearches = (n) => axios.get(`${API_BASE_URL}/words/top/${n}`);

// Search words by tags
export const searchWordsByTags = (tags) => axios.get(`${API_BASE_URL}/words/search`, { params: { tags } });

// Fetch all tags
export const fetchTags = () => axios.get(`${API_BASE_URL}/tags`);
